const myLoader = Rocket.loader( {
    colour: 'purple',
    target: '#loading',
    size: 'large',
    type: 'spinner',
} );

function CloseLoading() {
    $( '#loading' ).css( 'display', 'none' );
    $( '.mask' ).css( 'display', 'none' );
    //Rocket.event.add( () => { myLoader.remove(); } );
    myLoader.remove()
}

$( 'body' ).keydown( CloseLoading );